	<?php	// Category and Columns Counter	$iBrowseCol = 1;	$iBrowseProduct = 1;	// Calculating Products Per Row	$BrowseProducts_per_row = $this->perRow;	$Browsecellwidth = ' width' . floor (100 / $BrowseProducts_per_row);	// Separator	$verticalseparator = " vertical-separator";	$BrowseTotalProducts = count($this->products);	// Start the Output	foreach ($this->products as $product) {	?>		<?php		// this is an indicator wether a row needs to be opened or not		if ($iBrowseCol == 1) {			?>	<div class="row">	<?php		}		// Show the vertical seperator		if ($iBrowseProduct == $BrowseProducts_per_row or $iBrowseProduct % $BrowseProducts_per_row == 0) {			$show_vertical_separator = ' ';		} else {			$show_vertical_separator = $verticalseparator;		}		// Show Products		?>		<div class="product floatleft<?php echo $Browsecellwidth . $show_vertical_separator ?>">			<div class="spacer">			<div class="spacer_wr">				<div class="center">				    <a title="<?php echo $product->product_name ?>" rel="vm-additional-images" href="<?php echo $product->link; ?>">						<?php							echo $product->images[0]->displayMediaThumb('class="browseProductImage"', false);						?>					 </a>					<!-- The "Average Customer Rating" Part -->					<?php // Output: Average Product Rating					if ($this->showRating) {						$maxrating = VmConfig::get('vm_maximum_rating_scale', 5);						if (empty($product->rating)) {							?>							<span class="vote"><?php echo JText::_('COM_VIRTUEMART_RATING') . ' ' . JText::_('COM_VIRTUEMART_UNRATED') ?></span>						<?php						} else {							$ratingwidth = $product->rating * 12; //I don't use round as percetntage with works perfect, as for me							?>							<span class="vote">                                <?php echo JText::_('COM_VIRTUEMART_RATING') . ' ' . round($product->rating) . '/' . $maxrating; ?><br/>                                <span title=" <?php echo (JText::_("COM_VIRTUEMART_RATING_TITLE") . round($product->rating) . '/' . $maxrating) ?>" class="category-ratingbox" style="display:inline-block;">                                    <span class="stars-orange" style="width:<?php echo $ratingwidth.'px'; ?>">                                    </span>                                </span>                            </span>						<?php						}					}					if ( VmConfig::get ('display_stock', 1)) { ?>						<!-- 						if (!VmConfig::get('use_as_catalog') and !(VmConfig::get('stockhandle','none')=='none')){?> -->						<div class="paddingtop8">							<span class="vmicon vm2-<?php echo $product->stock->stock_level ?>" title="<?php echo $product->stock->stock_tip ?>"></span>							<span class="stock-level"><?php echo JText::_ ('COM_VIRTUEMART_STOCK_LEVEL_DISPLAY_TITLE_TIP') ?></span>						</div>					<?php } ?>				</div>				<div class="prod_ttl">					<h3><?php echo JHTML::link ($product->link, $product->product_name); ?></h3>										<div class="mf_name"><?=$product->mf_name?></div>					<div class="product-price marginbottom12" id="productPrice<?php echo $product->virtuemart_product_id ?>">						<?php						if ($this->show_prices == '1') {							if ($product->prices['salesPrice']<=0 and VmConfig::get ('askprice', 1) and  !$product->images[0]->file_is_downloadable) {								echo JText::_ ('COM_VIRTUEMART_PRODUCT_ASKPRICE');							}							//todo add config settings							if ($this->showBasePrice) {								echo $this->currency->createPriceDiv ('basePrice', 'COM_VIRTUEMART_PRODUCT_BASEPRICE', $product->prices);								echo $this->currency->createPriceDiv ('basePriceVariant', 'COM_VIRTUEMART_PRODUCT_BASEPRICE_VARIANT', $product->prices);							}							echo $this->currency->createPriceDiv ('variantModification', 'COM_VIRTUEMART_PRODUCT_VARIANT_MOD', $product->prices);							if (round($product->prices['basePriceWithTax'],$this->currency->_priceConfig['salesPrice'][1]) != $product->prices['salesPrice']) {								echo '<div class="price-crossed" >' . $this->currency->createPriceDiv ('basePriceWithTax', 'COM_VIRTUEMART_PRODUCT_BASEPRICE_WITHTAX', $product->prices) . "</div>";							}							if (round($product->prices['salesPriceWithDiscount'],$this->currency->_priceConfig['salesPrice'][1]) != $product->prices['salesPrice']) {								echo $this->currency->createPriceDiv ('salesPriceWithDiscount', 'COM_VIRTUEMART_PRODUCT_SALESPRICE_WITH_DISCOUNT', $product->prices);							}							//echo $this->currency->createPriceDiv ('salesPrice', 'COM_VIRTUEMART_PRODUCT_SALESPRICE', $product->prices);							echo $this->currency->createPriceDiv ('salesPrice', '', $product->prices);							if ($product->prices['discountedPriceWithoutTax'] != $product->prices['priceWithoutTax']) {								echo $this->currency->createPriceDiv ('discountedPriceWithoutTax', 'COM_VIRTUEMART_PRODUCT_SALESPRICE_WITHOUT_TAX', $product->prices);							} else {								echo $this->currency->createPriceDiv ('priceWithoutTax', 'COM_VIRTUEMART_PRODUCT_SALESPRICE_WITHOUT_TAX', $product->prices);							}							echo $this->currency->createPriceDiv ('discountAmount', 'COM_VIRTUEMART_PRODUCT_DISCOUNT_AMOUNT', $product->prices);							echo $this->currency->createPriceDiv ('taxAmount', 'COM_VIRTUEMART_PRODUCT_TAX_AMOUNT', $product->prices);							$unitPriceDescription = JText::sprintf ('COM_VIRTUEMART_PRODUCT_UNITPRICE', $product->product_unit);							echo $this->currency->createPriceDiv ('unitPrice', $unitPriceDescription, $product->prices);						} ?>								<?php		// Availability		$stockhandle = VmConfig::get('stockhandle', 'none');		$product_available_date = substr($product->product_available_date,0,10);		$current_date = date("Y-m-d");		if (($product->product_in_stock - $product->product_ordered) < 1) {			if ($product_available_date != '0000-00-00' and $current_date < $product_available_date) {			?>	<div class="availability">					<?php echo JText::_('COM_VIRTUEMART_PRODUCT_AVAILABLE_DATE') .': '. JHTML::_('date', $product->product_available_date, JText::_('DATE_FORMAT_LC4')); ?>				</div>		    <?php			} else if ($stockhandle == 'risetime' and VmConfig::get('rised_availability') and empty($product->product_availability)) {			?>	<div class="availability">			    <?php echo (file_exists(JPATH_BASE . DS . VmConfig::get('assets_general_path') . 'images/availability/' . VmConfig::get('rised_availability'))) ? JHTML::image(JURI::root() . VmConfig::get('assets_general_path') . 'images/availability/' . VmConfig::get('rised_availability', '7d.gif'), VmConfig::get('rised_availability', '7d.gif'), array('class' => 'availability')) : JText::_(VmConfig::get('rised_availability')); ?>			</div>		    <?php			} else if (!empty($product->product_availability)) {			?>			<div class="availability">			<?php echo (file_exists(JPATH_BASE . DS . VmConfig::get('assets_general_path') . 'images/availability/' . $product->product_availability)) ? JHTML::image(JURI::root() . VmConfig::get('assets_general_path') . 'images/availability/' . $product->product_availability, $product->product_availability, array('class' => 'availability')) : JText::_($product->product_availability); ?>			</div>			<?php			}		}		else if ($product_available_date != '0000-00-00' and $current_date < $product_available_date) {		?>	<div class="availability">				<?php echo JText::_('COM_VIRTUEMART_PRODUCT_AVAILABLE_DATE') .': '. JHTML::_('date', $product->product_available_date, JText::_('DATE_FORMAT_LC4')); ?>			</div>								<?php		}		//echo'<pre>';print_r($product->product_in_stock);echo'</pre>';				switch($product->product_in_stock)	{			case -1:				//echo '<span class="in_stock_status">Под заказ</span>';				$availability = 'zak.jpg';				break;			case 0:				//echo '<span class="in_stock_status">Нет на складе</span>';				$availability = 'net.jpg';				break;			default:				$availability = 'vnal.jpg';				break;		}		?>			<div class="availability">				<img src="/images/system/<?=$availability?>" class="availability">			</div>							<div class="addtocart-area">					<form method="post" class="product js-recalculate" action="<?php echo JRoute::_ ('index.php'); ?>">						<div class="addtocart-bar">							<?//<input type="text" class="quantity-input js-recalculate" name="quantity[]" value="<?=$product->min_order_level ? $product->min_order_level : '1'?1>"/>	?>							<input type="hidden" class="quantity-input" name="quantity[]" value="<?=$product->min_order_level ? $product->min_order_level : '1'?>"/>							<span class="addtocart-button">								<?php echo shopFunctionsF::getAddToCartButton ($product->orderable); ?>								<input type="hidden" name="product_in_cart" value="<?=$product->product_in_cart?>" />							</span>													</div>						<input type="hidden" class="pname" value="<?php echo htmlentities($product->product_name, ENT_QUOTES, 'utf-8') ?>"/>						<input type="hidden" name="option" value="com_virtuemart"/>						<input type="hidden" name="view" value="cart"/>						<noscript><input type="hidden" name="task" value="add"/></noscript>						<input type="hidden" name="virtuemart_product_id[]" value="<?php echo $product->virtuemart_product_id ?>"/>					</form>				</div>				<?				?>											</div>				</div>			</div>			</div>			<!-- end of spacer -->		</div> <!-- end of product -->		<?php		// Do we need to close the current row now?		if ($iBrowseCol == $BrowseProducts_per_row || $iBrowseProduct == $BrowseTotalProducts) {			?>			<div class="clear"></div>   </div> <!-- end of row -->			<?php			$iBrowseCol = 1;		} else {			$iBrowseCol++;		}		$iBrowseProduct++;	} // end of foreach ( $this->products as $product )	// Do we need a final closing row tag?	if ($iBrowseCol != 1) {		?>	<div class="clear"></div>		<?php	}		//echo'<pre>';print_r($this->vmPagination->limitstart);echo'</pre>';	//echo'<pre>';print_r($this->vmPagination);echo'</pre>';	?><div class="pagination_wr">	<div class="vm-pagination"><?php echo $this->vmPagination->getPagesLinks (); ?></div>	<input type="hidden" id="loaded_max" value="0" />	<? $pages_total = 'pages.total'; ?>	<? $pages_current = 'pages.current'; ?>	<input type="hidden" id="pages_total" value="<?=$this->vmPagination->$pages_total?>" />	<input type="hidden" id="pages_current" value="<?=$this->vmPagination->$pages_current?>" />	<input type="hidden" id="start" value="<?=$this->vmPagination->$pages_current * $this->vmPagination->limit?>" />	</div>